clear allclose alldata = csvread('41-Data.csv');data = data(2:end,:); % phi *, alpha *, Uparallel in mV, s_Up, Usenkrecht in mV, s_Us% Verst채rkung von 1e1 f체r alle gleich, also egal f체r Verh채ltnisdataUs = data(1:end-4,[1,5,6]);dataUp = data(:, [1,3,4]);[s, I] = sort(dataUp(:,1));dataUp = dataUp(I,:);Uep = 0.67673*1e+4 %VUes = 0.6431*1e+4n2 = tan(54*(pi/180))^2phiradp = dataUp(:,1)*(pi/180);phirads = dataUs(:,1)*(pi/180);%%%% Fehlerberechnungs_Ep = 0.5*sqrt( (0.05)^2 + ((0.0002.*dataUp(:,2))+0.04).^2 );s_Es = 0.5*sqrt( (0.05)^2 + ((0.0002.*dataUs(:,2))+0.04).^2 );s_phi = sqrt((0.707)^2+0.5^2);Ep = (dataUp(:,2)/Uep).^(1/2);Es = (dataUs(:,2)/Ues).^(1/2);theop = abs((n2*cos(phiradp)-sqrt(n2-sin(phiradp).^2))./(n2*cos(phiradp)+sqrt(n2-sin(phiradp).^2)));theos = abs((cos(phirads)-sqrt(n2-sin(phirads).^2))./(cos(phirads)+sqrt(n2-sin(phirads).^2)));fig = figure('DefaultAxesFontSize',18);pl1 = errorbar(dataUp(:,1), Ep, s_phi ,">xb");hold onpl2 = errorbar(dataUs(:,1), Es, s_phi ,">xr");pl3 = plot(dataUp(:,1), theop, '--b');pl4 = plot(dataUs(:,1), theos, '--r');xlabel('{\it \phi} in ^{\circ}')ylabel('|{\it E}_{r} / {\it E}_{e} |', 'Interpreter', 'Tex')nameMessParallel = '|{\it E}_{r} / {\it E}_{e} |_{||, mess}';nameMessSenk = '| {\it E}_{r} / {\it E}_{e} |_{\perp, mess}';nameTheoParallel = '| {\it E}_{r} / {\it E}_{e} |_{||, theo}';nameTheoSenk = '|{\it E}_{r} / {\it E}_{e} |_{\perp, theo}';leg = legend( nameMessParallel, nameMessSenk, nameTheoParallel, nameTheoSenk, 'location', 'northwest');set(leg, 'Interpreter', 'tex')set(pl1, 'LineWidth', 2)set(pl2, 'LineWidth', 2)set(pl3, 'LineWidth', 2)set(pl4, 'LineWidth', 2)set(fig, 'Position', [100 100 1200 800] ,'DefaultAxesFontSize',18)